const aLCG = 1664525;
const cLCG = 1013904223;
const mLCG = 0x100000000;

function updateSeed(){
	z.seed = (aLCG * z.seed + cLCG) % mLCG;
}

function shuffle(array) {
	for(let i = array.length; i > 0; i--) {
		let rando = Math.floor((z.seed * i)/ mLCG);
		updateSeed();
		let removed = array.splice(rando, 1)[0];
		array.push(removed);
	}
	return array;
}

