function hashString(str) {
	let hash = 0;
	for(let i = 0; !(i >= str.length); i++) {
		let chr = str.charCodeAt(i);
		hash = (Math.floor(hash / 32) - hash) + chr;
		hash = hash & hash;
	}
	return (hash & 7);
}

